llapse() {        if ($(".submenu").css('display') == 'none') {            // $("#expand-collapse").html("Collapse All");            $(".submenu").show("slow");        } else {            // $("#expand-collapse").html("Expand All");            $(".submenu").hide("slow");        }    }    function expandCollapseByClass(id) {        if ($(".submenu_" + id).css('display') == 'none') {            // $("#expand-collapse").html("Collapse All");            $(".submenu_" + id).show("slow");        } else {            // $("#expand-collapse").html("Expand All");            $(".submenu_" + id).hide("slow");        }    }    $(function () {        $('.do_parent_in').parent().parent().show("slow");    });</script>